import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './ScheduleForm.css';

const ScheduleForm = () => {
    const { id } = useParams(); // Property ID from URL
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        date: '',
        time: '',
        message: '',
    });
    const [submissionStatus, setSubmissionStatus] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:5000/api/schedule', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ ...formData, propertyId: id }),
            });

            if (response.ok) {
                setSubmissionStatus('Your schedule request has been submitted successfully!');
                setFormData({
                    name: '',
                    email: '',
                    phone: '',
                    date: '',
                    time: '',
                    message: '',
                });
            } else {
                setSubmissionStatus('Failed to submit schedule request. Please try again.');
            }
        } catch (error) {
            setSubmissionStatus('An error occurred. Please try again later.');
        }
    };

    return (
        <div className="schedule-form">
            <h2>Schedule a Viewing</h2>
            {submissionStatus && <p className="status-message">{submissionStatus}</p>}
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </label>
                <label>
                    Email:
                    <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                </label>
                <label>
                    Phone:
                    <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
                </label>
                <label>
                    Date:
                    <input type="date" name="date" value={formData.date} onChange={handleChange} required />
                </label>
                <label>
                    Time:
                    <input type="time" name="time" value={formData.time} onChange={handleChange} required />
                </label>
                <label>
                    Message:
                    <textarea name="message" value={formData.message} onChange={handleChange}></textarea>
                </label>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default ScheduleForm;
