import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './PropertyDetail.css';

const PropertyDetail = () => {
  const { id } = useParams(); // Get property ID from URL

  const [showForm, setShowForm] = useState(false);
  const [scheduleData, setScheduleData] = useState({
    name: '',
    phone: '',
    email: '',
    date: '',
    time: '',
  });

  const [scheduleSuccess, setScheduleSuccess] = useState(null);

  const listings = [
    {
      id: 1,
      type: 'Apartment',
      location: 'Shivtirth Nagar, Kothrud, Pune',
      price: '$1500/month',
      area: '1200 sq ft',
      bedrooms: 2,
      bathrooms: 2,
      balcony: 'Yes',
      parking: 'Available',
      amenities: {
        basic: ['Water Supply', 'Electricity', 'Gas'],
        security: ['Gated Community', 'CCTV'],
        lifestyle: ['Swimming Pool', 'Gym', 'Club House'],
        additional: ['Garden', 'Playground', 'Community Hall'],
      },
      photos: ['/images/Shivtirth Nagar1.jpg', '/images/Shivtirth Nagar2.jpg'],
      description: 'A beautiful 2-bedroom apartment located in Shivtirth Nagar with modern amenities and a serene environment.',
      neighborhood: 'Nearby schools, hospitals, and shopping centers.',
      transportation: 'Well-connected by public transport and close to major highways.',
      contact: {
        phone: '123-456-7890',
        email: 'agent@example.com',
      },
    },
    // Add more listings with different IDs
  ];

  const property = listings.find((listing) => listing.id === parseInt(id));

  if (!property) {
    return <p>Property not found</p>;
  }

  const handleInputChange = (e) => {
    setScheduleData({ ...scheduleData, [e.target.name]: e.target.value });
  };

  const handleSchedule = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/schedule', { ...scheduleData, propertyId: id });
      setScheduleSuccess(true);
      setScheduleData({
        name: '',
        phone: '',
        email: '',
        date: '',
        time: '',
      });
    } catch (error) {
      setScheduleSuccess(false);
    }
  };

  return (
    <div className="property-detail">
      <h1>{property.type} in {property.location}</h1>
      <p className="price">{property.price}</p>
      <div className="property-images">
        {property.photos.map((photo, index) => (
          <img key={index} src={photo} alt={`Property ${index}`} />
        ))}
      </div>
      <div className="property-info">
        <h2>Property Details</h2>
        <p>Area: {property.area}</p>
        <p>Bedrooms: {property.bedrooms}</p>
        <p>Bathrooms: {property.bathrooms}</p>
        <p>Balcony/Terrace: {property.balcony}</p>
        <p>Parking: {property.parking}</p>
      </div>
      <div className="amenities">
        <h2>Amenities</h2>
        <ul>
          <li><strong>Basic:</strong> {property.amenities.basic.join(', ')}</li>
          <li><strong>Security:</strong> {property.amenities.security.join(', ')}</li>
          <li><strong>Lifestyle:</strong> {property.amenities.lifestyle.join(', ')}</li>
          <li><strong>Additional:</strong> {property.amenities.additional.join(', ')}</li>
        </ul>
      </div>
      <div className="description">
        <h2>Description</h2>
        <p>{property.description}</p>
        <p>{property.neighborhood}</p>
        <p>{property.transportation}</p>
      </div>
      <div className="contact">
        <h2>Contact Information</h2>
        <p>Phone: {property.contact.phone}</p>
        <p>Email: <a href={`mailto:${property.contact.email}`}>{property.contact.email}</a></p>
      </div>
      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Cancel' : 'Schedule a Viewing'}
      </button>
      {showForm && (
        <div className="schedule-viewing">
          <h2>Schedule a Viewing</h2>
          <form onSubmit={handleSchedule}>
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              value={scheduleData.name}
              onChange={handleInputChange}
              required
            />
            <input
              type="tel"
              name="phone"
              placeholder="Your Phone Number"
              value={scheduleData.phone}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              value={scheduleData.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="date"
              name="date"
              value={scheduleData.date}
              onChange={handleInputChange}
              required
            />
            <input
              type="time"
              name="time"
              value={scheduleData.time}
              onChange={handleInputChange}
              required
            />
            <button type="submit">Submit</button>
          </form>
          {scheduleSuccess === true && <p className="success-message">Viewing scheduled successfully!</p>}
          {scheduleSuccess === false && <p className="error-message">Error scheduling the viewing. Please try again.</p>}
        </div>
      )}
    </div>
  );
};

export default PropertyDetail;
