import React from 'react';
import Listing from '../components/Listing';
import './Home.css';

const listings = [
  {
    id: 1,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'Shivtirth Nagar, Kothrud',
    price: '$1500/month',
    image: '/images/background.jpg', // Correct path for public directory
  },
  {
    id: 2,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'Paud , Pune',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  {
    id: 3,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'Gokhale Nagar, Baner',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  {
    id: 4,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'Punawale Corner, Pashan',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  {
    id: 5,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'F C Road, Pune',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  {
    id: 6,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'JM Road, Pune',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  {
    id: 7,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'Shivaji Nagar, Hadapsar',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  {
    id: 8,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'Dange Chauk, PCMC',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  {
    id: 9,
    title: 'Beautiful 2 Bedroom Apartment',
    location: 'Swargate, Pune',
    price: '$1500/month',
    image: '/images/background.jpg',
  },
  // Add more listings as needed
];

function Home() {
  const rows = [listings.slice(0, 6), listings.slice(6, 12), listings.slice(12, 18)];

  return (
    <div className="container">
<h1 style={{ textAlign: 'center', margin: '20px 0', fontSize: '2em', color: '#333' }}>
  Find Your Perfect Home In Pune
</h1>
{rows.map((row, rowIndex) => (
        <div className="flex-container" key={rowIndex}>
          {row.map(listing => (
            <Listing key={listing.id} listing={listing} />
          ))}
        </div>
      ))}
    </div>
  );
}

export default Home;
