// src/components/Listing.js
import React from 'react';
import '../pages/Home.css'; // Import the CSS file

const Listing = () => {
    // Array with different data for each item
    const items = [
        {
            title: "Luxury Apartment",
            description: "Spacious 3 bedroom apartment with city view.",
            price: "$750,000",
            image: "D:\Affordable House Finder\frontend\public\images\background.jpg.jpg"
        },
        {
            title: "Cozy Cottage",
            description: "Charming 2 bedroom cottage with garden.",
            price: "$350,000",
            image: "cozy-cottage.jpg"
        },
        {
            title: "Modern Loft",
            description: "Stylish loft with open floor plan.",
            price: "$500,000",
            image: "modern-loft.jpg"
        },
        {
            title: "Beach House",
            description: "Beautiful house right on the beach.",
            price: "$1,200,000",
            image: "beach-house.jpg"
        },
        {
            title: "Urban Studio",
            description: "Compact studio in the heart of the city.",
            price: "$300,000",
            image: "urban-studio.jpg"
        },
        {
            title: "Family Home",
            description: "Large family home with backyard and pool.",
            price: "$900,000",
            image: "family-home.jpg"
        }
    ];

    return (
        <div className="container">
            <h1>Property Listings</h1>
            <div className="flex-row-container">
                {items.map((item, index) => (
                    <div className="flex-row" key={index}>
                        {items.slice(index * 3, index * 3 + 3).map((item, itemIndex) => (
                            <div className="flex-item" key={itemIndex}>
                                <img src={item.image} alt={item.title} />
                                <div className="flex-item-content">
                                    <h2>{item.title}</h2>
                                    <p>{item.description}</p>
                                    <p className="price">{item.price}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Listing;
