import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './ScheduleForm.css';

const ScheduleForm = () => {
    const { id } = useParams(); // Property ID from URL
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        date: '',
        time: '',
        message: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const response = await fetch('http://localhost:5000/api/schedule', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ ...formData, propertyId: id }),
        });

        if (response.ok) {
            alert('Schedule request submitted successfully');
            // Optionally, navigate the user to a confirmation page or reset the form
        } else {
            alert('Failed to submit schedule request');
        }
    };

    return (
        <div className="schedule-form">
            <h2>Schedule a Viewing</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </label>
                <label>
                    Email:
                    <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                </label>
                <label>
                    Phone:
                    <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
                </label>
                <label>
                    Date:
                    <input type="date" name="date" value={formData.date} onChange={handleChange} required />
                </label>
                <label>
                    Time:
                    <input type="time" name="time" value={formData.time} onChange={handleChange} required />
                </label>
                <label>
                    Message:
                    <textarea name="message" value={formData.message} onChange={handleChange}></textarea>
                </label>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default ScheduleForm;
