// src/components/Listing.js
import React from 'react';
import '../pages/Home.css'; // Import the CSS file

const Listing = () => {
    // Array with different data for each item
    const items = [
        {
            title: "Luxury Apartment",
            description: "Spacious 3 bedroom apartment with city view.",
            price: "$750,000",
            location: "Downtown City",
            image: "/images/background.jpg" // Correct path for images in public directory
        },
        {
            title: "Cozy Cottage",
            description: "Charming 2 bedroom cottage with garden.",
            price: "$350,000",
            location: "Countryside",
            image: "/images/cozy-cottage.jpg"
        },
        {
            title: "Modern Loft",
            description: "Stylish loft with open floor plan.",
            price: "$500,000",
            location: "Urban Area",
            image: "/images/modern-loft.jpg"
        },
        {
            title: "Beach House",
            description: "Beautiful house right on the beach.",
            price: "$1,200,000",
            location: "Beachfront",
            image: "/images/beach-house.jpg"
        },
        {
            title: "Urban Studio",
            description: "Compact studio in the heart of the city.",
            price: "$300,000",
            location: "City Center",
            image: "/images/urban-studio.jpg"
        },
        {
            title: "Family Home",
            description: "Large family home with backyard and pool.",
            price: "$900,000",
            location: "Suburban Area",
            image: "/images/family-home.jpg"
        }
    ];

    return (
        <div className="container">
            <h1>Property Listings</h1>
            <div className="flex-row-container">
                {items.map((item, index) => (
                    <div className="flex-item" key={index}>
                        <img src={item.image} alt={item.title} />
                        <div className="flex-item-content">
                            <h2>{item.title}</h2>
                            <p>{item.description}</p>
                            <p className="price"><strong>{item.price}</strong></p>
                            <p className="location">{item.location}</p> {/* Location field */}
                            <button className="view-details-button">View Details</button> {/* View Details button */}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Listing;
