// src/pages/ScheduleForm.js
import React, { useState } from 'react';
import './ScheduleForm.css';

const ScheduleForm = ({ setSuccessMessage }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [date, setDate] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/schedule', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, phone, date, message }),
      });

      if (response.ok) {
        setSuccessMessage('Schedule request submitted successfully!');
        setName('');
        setEmail('');
        setPhone('');
        setDate('');
        setMessage('');
      } else {
        throw new Error('Failed to submit schedule request.');
      }
    } catch (error) {
      console.error(error);
      setSuccessMessage('An error occurred. Please try again later.');
    }
  };

  return (
    <form className="schedule-form" onSubmit={handleSubmit}>
      <h2>Schedule a Viewing</h2>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </label>
      <label>
        Email:
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      </label>
      <label>
        Phone:
        <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} required />
      </label>
      <label>
        Preferred Date:
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
      </label>
      <label>
        Message:
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default ScheduleForm;
