// src/pages/ScheduleForm.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './ScheduleForm.css';

const ScheduleForm = () => {
  const { id } = useParams(); // Fetch the property ID from the URL
  const [successMessage, setSuccessMessage] = useState(''); // Initialize state

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform form submission logic here

    // Set a success message
    setSuccessMessage('Your viewing has been scheduled successfully!');
  };

  return (
    <div className="schedule-form">
      <h1>Schedule a Viewing</h1>
      {successMessage && <p className="success-message">{successMessage}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" name="name" required />
        </label>
        <label>
          Phone:
          <input type="text" name="phone" required />
        </label>
        <label>
          Preferred Date:
          <input type="date" name="date" required />
        </label>
        <label>
          Preferred Time:
          <input type="time" name="time" required />
        </label>
        <input type="hidden" name="propertyId" value={id} />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ScheduleForm;
